<script>
    import { onMount } from 'svelte';
    import { onInterval } from '../utils/util.js';
    import * as d3 from 'd3';
    import * as m from 'ml-matrix';

    const width = 400;
    const height = 400;
    const res = 100;
    const scale = width / res;

    let contours_nonlinear, contours_linear;
    let values_nonlinear = new Array(res * res);
    let values_linear = new Array(res * res);

    const thresholds = Array.from({ length: 3 }, (_, i) => (i+1)*0.3);

    const r_pos = [0., 0.];
    const l_pos = [2., 2.];
    const x_range = [-2.5, 6.]
    const y_range = [-2.5, 6.]

    const r_x = (r_pos[0]-x_range[0]) / (x_range[1]-x_range[0]) * width;
    const r_y = height - (r_pos[1]-y_range[0]) / (y_range[1]-y_range[0]) * height;
    const l_x = (l_pos[0]-x_range[0]) / (x_range[1]-x_range[0]) * width;
    const l_y = height - (l_pos[1]-y_range[0]) / (y_range[1]-y_range[0]) * height;

    const meas = meas_fn(r_pos, l_pos);

    let ang_sigma = 0.5;
    let dist_sigma = 0.5;
    let meas_lam = new m.Matrix([[1/ang_sigma, 0.], [0., 1/dist_sigma]]);


    // Prepare a color palette
    var color_nonlinear = d3.scaleLinear()
        .domain([0, 1]) // Points per square pixel.
        .range(["white", "#69b3a2"])

    function map_coords(coordinates) {
        return coordinates.map(x => [x[0]*scale, height - x[1]*scale]);
    }

    // r is robot location, l i landmark location
    function meas_fn(r, l) {
        const ang = Math.atan2((l[1] - r[1]),(l[0] - r[0]))
        const dist = Math.sqrt((l[0] - r[0])*(l[0] - r[0]) + (l[1] - r[1])*(l[1] - r[1]));
        return new m.Matrix([[ang, dist]]);
    }

    function jac_fn(r, l) {
        const x1 = r[0];
        const y1 = r[1];
        const x2 = l[0];
        const y2 = l[1];

        const d = Math.sqrt((x2-x1)**2 + (y2-y1)**2);
        const denominator = (x2-x1)**2 + (y2-y1)**2;
        let jac = m.Matrix.zeros(2, 4);
        jac.set(0, 0, (y2-y1) / denominator);
        jac.set(0, 1, -(x2-x1) / denominator);
        jac.set(0, 2, -(y2-y1) / denominator);
        jac.set(0, 3, (x2-x1) / denominator);
        jac.set(1, 0, -(x2-x1) / d);
        jac.set(1, 1, -(y2-y1) / d);
        jac.set(1, 2, (x2-x1) / d);
        jac.set(1, 3, (y2-y1) / d);
        return jac;
    }

    function nonlinear_factor(r, l, meas, meas_lam) {
        const pred_meas = meas_fn(r, l);
        const diff = m.Matrix.sub(meas, pred_meas);
        return Math.exp(-0.5 * diff.mmul(meas_lam).mmul(diff.transpose()).get(0, 0));
    }

    function conditional_nonlinear_factor(l0, l1) {
        return nonlinear_factor(r_pos, [l0, l1], meas, meas_lam);
    }

    function linear_factor_params(r_lin, l_lin, meas) {
        const linpoint = new m.Matrix([[r_lin[0], r_lin[1], l_lin[0], l_lin[1]]]).transpose();
        const jac = jac_fn(r_lin, l_lin)
        const pred_meas = meas_fn(r_lin, l_lin);
        const diff = m.Matrix.sub(meas, pred_meas).transpose();

        const lam = jac.transpose().mmul(meas_lam).mmul(jac);
        const eta = jac.transpose().mmul(meas_lam).mmul(m.Matrix.add(jac.mmul(linpoint), diff));
        return [eta, lam];

    }

    // Just evaluates information form Gaussian
    function conditional_linear_factor(l0, l1, eta, lam) {
        const x = new m.Matrix([[r_pos[0], r_pos[1], l0, l1]]).transpose();
        return Math.exp(-0.5 * x.transpose().mmul(lam).mmul(x).get(0, 0) + eta.transpose().mmul(x).get(0, 0));
    }

    function load_contours() {
        const label = String(ang_sigma.toFixed(2)) + "-" + String(dist_sigma.toFixed(2));
        contours_nonlinear = contour_dict[label][0];
        contours_linear = contour_dict[label][1];
    }

    function compute_contours() {

        let time1 = Date.now();

        // Use true position of robot and landmark as linearisation point
        const [eta, lam] = linear_factor_params(r_pos, l_pos, meas);
        const norm_factor = conditional_linear_factor(l_pos[0], l_pos[1], eta, lam)

        for (var j = 0.5, k = 0; j < res; ++j) {
            for (var i = 0.5; i < res; ++i, ++k) {
                const lx = x_range[0] + i / res * (x_range[1]-x_range[0]);
                const ly = y_range[0] + j / res * (y_range[1]-y_range[0]);
                values_nonlinear[k] = conditional_nonlinear_factor(lx, ly);
                values_linear[k] = conditional_linear_factor(lx, ly, eta, lam) / norm_factor;
            }
        }

        // Compute the contour polygons at thresholds; returns an array of MultiPolygon.
        contours_nonlinear = d3.contours()
            .size([res, res])
            .thresholds(thresholds)
            (values_nonlinear);
        contours_linear = d3.contours()
            .size([res, res])
            .thresholds(thresholds)
            (values_linear);

        let time2 = Date.now();
        console.log((time2 - time1)/ 1000)
    }

    function precompute() {
        let contour_dict = {}
        for (var ang_sig = 0.25; ang_sig < 1.05; ang_sig += 0.05) {
            for (var dist_sig = 0.25; dist_sig < 1.05; dist_sig += 0.05) {
                const label = String(ang_sig.toFixed(2)) + "-" + String(dist_sig.toFixed(2))
                dist_sigma = dist_sig;
                ang_sigma = ang_sig;
                compute_contours();
                for (var i=0; i < contours_nonlinear.length; ++i) {
                    contours_nonlinear[i].coordinates = contours_nonlinear[i].coordinates.map(x => [(x[0]*scale).toFixed(2), (height - x[1]*scale).toFixed(2)]);
                    contours_linear[i].coordinates = contours_linear[i].coordinates.map(x => [(x[0]*scale).toFixed(2), (height - x[1]*scale).toFixed(2)]);
                }
                contour_dict[label] = [contours_nonlinear, contours_linear];
            }
        }
        console.log(JSON.stringify(contour_dict));
        console.log(contour_dict);
    }

    onMount(() => {
        // precompute();
        compute_contours();
        // load_contours()
    });

    onInterval(() => {
        // Check variances have not been changed
        if (meas_lam.get(0, 0) != 1./ang_sigma || meas_lam.get(1, 1) != 1./dist_sigma) {
            meas_lam = new m.Matrix([[1/ang_sigma, 0.], [0., 1/dist_sigma]]);
            compute_contours();
            // load_contours()
        }
    }, 1000 / 30);


</script>

